import React, { useEffect, useState } from "react";
import signalRService from "../services/signalRService";

const TerminalComponent = () => {
  const [legs, setLegs] = useState([]);

  useEffect(() => {
    const startSignalRConnection = async () => {
      await signalRService.startConnection();

      // Handle flightReceived event
      signalRService.addFlightReceivedListener((flight) => {
        console.log("Received flight:", flight);
        updateLeg(flight.currentLeg, flight);
      });

      // Handle flightRemove event
      signalRService.addFlightRemoveListener((flightId) => {
        console.log("Removed flight:", flightId);
        removeFlight(flightId);
      });

      // Handle flightMove event
      signalRService.addFlightMoveListener((flightId, updatedLeg) => {
        console.log(`Moved flight: ${flightId} - ${updatedLeg}`);
        moveFlight(flightId, updatedLeg);
      });
    };

    startSignalRConnection();
  }, []);

  const initializeLegs = () => {
    const initialLegs = [];
    for (let i = 1; i <= 9; i++) {
      initialLegs.push({ legNumber: i, flights: [] });
    }
    setLegs(initialLegs);
  };

  const updateLeg = (legNumber, flight) => {
    setLegs((prevLegs) =>
      prevLegs.map((leg) =>
        leg.legNumber === legNumber ? { ...leg, flights: [...leg.flights, flight] } : leg
      )
    );
  };

  const moveFlight = (flightId, updatedLeg) => {
    setLegs((prevLegs) =>
      prevLegs.map((leg) => {
        if (leg.legNumber === updatedLeg) {
          return { ...leg, flights: [...leg.flights, getFlightById(flightId)] };
        } else if (leg.flights.some((flight) => flight.flightId === flightId)) {
          return { ...leg, flights: leg.flights.filter((flight) => flight.flightId !== flightId) };
        } else {
          return leg;
        }
      })
    );
  };

  const removeFlight = (flightId) => {
    setLegs((prevLegs) =>
      prevLegs.map((leg) => {
        if (leg.flights.some((flight) => flight.flightId === flightId)) {
          return { ...leg, flights: leg.flights.filter((flight) => flight.flightId !== flightId) };
        } else {
          return leg;
        }
      })
    );
  };

  const getFlightById = (flightId) => {
    for (const leg of legs) {
      const flight = leg.flights.find((flight) => flight.flightId === flightId);
      if (flight) {
        return flight;
      }
    }
    return null;
  };

  useEffect(() => {
    initializeLegs();
  }, []);

  return (
    <div className="terminal">
      {legs.map((leg) => (
        <div className="leg" key={leg.legNumber}>
          <h2>Leg {leg.legNumber}</h2>
          {leg.flights.length > 0 ? (
            leg.flights.map((flight) => (
              <p key={flight.flightId}>Flight: {flight.flightId}</p>
            ))
          ) : (
            <p>No flights</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default TerminalComponent;
