import React, { useEffect, useState } from "react";
import signalRService from "../services/signalRService";


const TerminalComponent = () => {
  const [legs, setLegs] = useState([]);

  useEffect(() => {
    const startSignalRConnection = async () => {
      await signalRService.startConnection();

      signalRService.addFlightReceivedListener((flight) => {
        console.log("Received flight:", flight);
        handleFlightReceived(flight);
      });

      signalRService.addFlightRemoveListener((flightId) => {
        console.log("Removed flight:", flightId);
        handleFlightRemoved(flightId);
      });

      signalRService.addFlightMoveListener((flightId, updatedLeg) => {
        console.log(`Moved flight: ${flightId} - ${updatedLeg}`);
        handleFlightMoved(flightId, updatedLeg);
      });
    };

    startSignalRConnection();
  }, []);

  const handleFlightReceived = (flight) => {
    setLegs((prevLegs) => {
      const updatedLegs = [...prevLegs];

      // Find the leg matching the flight's current leg
      const legIndex = updatedLegs.findIndex(
        (leg) => leg.legNumber === flight.currentLeg
      );

      if (legIndex !== -1) {
        // Add the flight to the leg
        updatedLegs[legIndex].flights.push(flight);
      }

      return updatedLegs;
    });
  };

  const handleFlightRemoved = (flightId) => {
    setLegs((prevLegs) => {
      const updatedLegs = [...prevLegs];

      // Remove the flight from the leg
      updatedLegs.forEach((leg) => {
        leg.flights = leg.flights.filter((flight) => flight.flightId !== flightId);
      });

      return updatedLegs;
    });
  };

  const handleFlightMoved = (flightId, updatedLeg) => {
    setLegs((prevLegs) => {
      const updatedLegs = [...prevLegs];

      // Find the leg matching the updated leg
      const legIndex = updatedLegs.findIndex(
        (leg) => leg.legNumber === updatedLeg
      );

      if (legIndex !== -1) {
        // Find the flight matching the flightId
        const flightIndex = updatedLegs[legIndex].flights.findIndex(
          (flight) => flight.flightId === flightId
        );

        if (flightIndex !== -1) {
          // Update the flight's current leg
          updatedLegs[legIndex].flights[flightIndex].currentLeg = updatedLeg;
        }
      }

      return updatedLegs;
    });
  };

  return (
    <div className="terminal">
      {legs.map((leg) => (
        <div className="leg" key={leg.legNumber}>
          <h2>Leg {leg.legNumber}</h2>
          {leg.flights.length > 0 ? (
            leg.flights.map((flight) => (
              <p key={flight.flightId} className="flight">
                Flight: {flight.flightId}
              </p>
            ))
          ) : (
            <p>No flights</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default TerminalComponent;
