import React, { useEffect, useState } from "react";
import signalRService from "../services/signalRService";

const TerminalComponent = () => {
  const [legs, setLegs] = useState([]);

  useEffect(() => {
    const startSignalRConnection = async () => {
      await signalRService.startConnection();

      // Handle flightReceived event
      signalRService.addFlightReceivedListener((flight) => {
        console.log("Received flight:", flight);
        updateLeg(flight.currentLeg, flight.flightNumber);
      });

      // Handle flightRemove event
      signalRService.addFlightRemoveListener((flightId) => {
        console.log("Removed flight:", flightId);
        removeFlight(flightId);
      });

      // Handle flightMove event
      signalRService.addFlightMoveListener((flight, updatedLeg) => {
        console.log(`Moved flight: ${flight.flightNumber} - ${updatedLeg}`);
        moveFlight(flight, updatedLeg);
      });
    };

    startSignalRConnection();
  }, []);

  const initializeLegs = () => {
    const initialLegs = [];
    for (let i = 1; i <= 9; i++) {
      initialLegs.push({ legNumber: i, flights: [] });
    }
    setLegs(initialLegs);
  };

  const updateLeg = (legNumber, flight) => {
    setLegs((prevLegs) =>
      prevLegs.map((leg) =>
        leg.legNumber === legNumber ? { ...leg, flights: [...leg.flights, flight] } : leg
      )
    );
  };
  
  const moveFlight = (flight, updatedLeg) => {
    setLegs((prevLegs) =>
      prevLegs.map((leg) =>
        leg.flights.some((f) => f.id === flight.id)
          ? { ...leg, flights: leg.flights.filter((f) => f.id !== flight.id) }
          : leg
      )
    );
    updateLeg(updatedLeg, flight);
  };
  

  const removeFlight = (flightId) => {
    setLegs((prevLegs) =>
      prevLegs.map((leg) => ({
        ...leg,
        flights: leg.flights.filter((flight) => flight.id !== flightId)
      }))
    );
  };
  

  useEffect(() => {
    initializeLegs();
  }, []);

  return (
    <div className="terminal">
      {legs.map((leg, index) => (
        <div key={`leg-${index + 1}`} className={index == 7 || index`line leg leg-${index + 1}`}>
          <h2>Leg {index + 1}</h2>
          {leg.flights.length > 0 ? (
            leg.flights.map((flight) => (
              <p key={flight.flightNumber}>Flight: {flight.flightNumber}</p>
            ))
          ) : (
            <p>No flights</p>
          )}
        </div>
      ))}
    </div>
  );
  
};

export default TerminalComponent;
