import React, { useEffect, useState } from "react";
import signalRService from "../services/signalRService";

const TerminalComponent = () => {
  const [legs, setLegs] = useState([]);

  useEffect(() => {
    const startSignalRConnection = async () => {
      await signalRService.startConnection();

      // Handle flightReceived event
      signalRService.addFlightReceivedListener((flight) => {
        console.log("Received flight:", flight);
        updateLeg(flight.currentLeg, flight.flightNumber);
      });

      // Handle flightRemove event
      signalRService.addFlightRemoveListener((flightId) => {
        console.log("Removed flight:", flightId);
        removeFlight(flightId);
      });

      // Handle flightMove event
      signalRService.addFlightMoveListener((flightId, updatedLeg) => {
        console.log(`Moved flight: ${flightId} - ${updatedLeg}`);
        moveFlight(flightId, updatedLeg);
      });
    };

    startSignalRConnection();
  }, []);

  const initializeLegs = () => {
    const initialLegs = [];
    for (let i = 1; i <= 9; i++) {
      initialLegs.push({ legNumber: i, flightNumber: null });
    }
    setLegs(initialLegs);
  };

  const updateLeg = (legNumber, flightNumber) => {
    setLegs((prevLegs) =>
      prevLegs.map((leg) =>
        leg.legNumber === legNumber ? { ...leg, flightNumber } : leg
      )
    );
  };

  const moveFlight = (flightId, updatedLeg) => {
    setLegs((prevLegs) =>
      prevLegs.map((leg) =>
        leg.flightNumber === flightId ? { ...leg, flightNumber: null } : leg
      )
    );
    updateLeg(updatedLeg, flightId);
  };

  const removeFlight = (flightId) => {
    setLegs((prevLegs) =>
      prevLegs.map((leg) =>
        leg.flightNumber === flightId ? { ...leg, flightNumber: null } : leg
      )
    );
  };

  useEffect(() => {
    initializeLegs();
  }, []);

  return (
    <div className="terminal">
      {legs.map((leg) => (
        <div className="leg" key={leg.legNumber}>
          <h2>Leg {leg.legNumber}</h2>
          {leg.flightNumber && <p>Flight: {leg.flightNumber}</p>}
        </div>
      ))}
    </div>
  );
};

export default TerminalComponent;
