import React, { useEffect, useState } from "react";
import signalRService from "../services/signalRService";

const TerminalComponent = () => {
  const [flights, setFlights] = useState([]);

  useEffect(() => {
    const startSignalRConnection = async () => {
      await signalRService.startConnection();

      signalRService.addFlightReceivedListener((flight) => {
        console.log("Received flight:", flight);
        setFlights((prevFlights) => [...prevFlights, flight]);
      });

      signalRService.addFlightRemoveListener((flightId) => {
        console.log("Removed flight:", flightId);
        setFlights((prevFlights) =>
          prevFlights.filter((flight) => flight.id !== flightId)
        );
      });

      signalRService.addFlightMoveListener((flightId, updatedLeg) => {
        console.log(`Moved flight: ${flightId} - ${updatedLeg}`);
        setFlights((prevFlights) =>
          prevFlights.map((flight) =>
            flight.id === flightId ? { ...flight, currentLeg: updatedLeg } : flight
          )
        );
      });
    };

    startSignalRConnection();
  }, []);

  const renderLegs = () => {
    return Array.from({ length: 9 }, (_, index) => {
      const legFlights = flights.filter((flight) => flight.currentLeg === index + 1);

      return (
        <div key={index} className="leg">
          <h3>Leg {index + 1}</h3>
          {legFlights.map((flight) => (
            <div key={flight.id} className="flight">
              Flight {flight.id}
            </div>
          ))}
        </div>
      );
    });
  };

  return <div className="terminal">{renderLegs()}</div>;
};

export default TerminalComponent;
